import $ from 'jquery';
import 'bootstrap';
import * as leaflet from './event/leaferMap';
import * as autoload from './event/algolia';

//the element name is generated by easy-admin
$('a[href="#_easyadmin_form_design_element_8"]').click(function () {
    leaflet.mymap.invalidateSize()
});


autoload.placesInstance.on('change', function (e) {
    autoload.resultSelected(e);
    leaflet.moveMarker();
});